import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css'; // Import leaflet styles

const Dashboard = () => {
    const [nodes, setNodes] = useState([]);

    useEffect(() => {
        // Fetch node data from NodesDashboard endpoint
        axios.get('http://127.0.0.1:5000/get_nodes')
            .then(response => {
                // Assuming response.data is an array of nodes with ip, country, latitude, and longitude
                setNodes(response.data);
            })
            .catch(error => console.error('Error fetching nodes:', error));
    }, []);

    return (
        <div>
            <h1>Node Dashboard</h1>
            <MapContainer center={[51.505, -0.09]} zoom={2} style={{ height: "500px", width: "100%" }}>
                <TileLayer
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                />
                {nodes.map((node, index) => (
                    <Marker key={index} position={[node.latitude, node.longitude]}>
                        <Popup>
                            IP: {node.ip}<br />
                            Country: {node.country}
                        </Popup>
                    </Marker>
                ))}
            </MapContainer>
        </div>
    );
};

export default Dashboard;
