import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../assets/Dashboard.css'; // Ensure this CSS file exists and is correctly styled

const Dashboard = () => {
    const [nodeCount, setNodeCount] = useState(0);
    const [transactionCount, setTransactionCount] = useState(0);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const nodeResponse = await axios.get('/api/nodes/count');
                setNodeCount(nodeResponse.data.count);

                const transactionResponse = await axios.get('/api/transactions/count');
                setTransactionCount(transactionResponse.data.count);
            } catch (error) {
                console.error('Error fetching data:', error);
                // Handle errors appropriately
            }
        };

        fetchData();
    }, []);

    return (
        <div className="dashboard">
            <div className="dashboard-item animated">
                <h2>Registered Nodes</h2>
                <p className="count">{nodeCount}</p>
            </div>
            <div className="dashboard-item animated">
                <h2>Transactions Occurred</h2>
                <p className="count">{transactionCount}</p>
            </div>
            {/* Add more dashboard items as needed */}
        </div>
    );
};

export default Dashboard;
