import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../assets/Dashboard.css'; // Ensure this CSS file exists and is correctly styled

function IPInfo() {
  const [ipAddress, setIpAddress] = useState('');
  const [country, setCountry] = useState('');
  const [loading, setLoading] = useState(false);

  const fetchCountryInfo = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`https://ipinfo.io/${get_nodes}/json`);
      const data = response.data;
      const countryName = data.country || 'Unknown';
      setCountry(countryName);
    } catch (error) {
      console.error('Error fetching country information:', error);
      setCountry('Error');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h1>IP Country Finder</h1>
      <div>
        <label>Enter an IP Address: </label>
        <input
          type="text"
          value={ipAddress}
          onChange={(e) => setIpAddress(e.target.value)}
        />
        <button onClick={fetchCountryInfo}>Find Country</button>
      </div>
      <div>
        {loading ? (
          <p>Loading...</p>
        ) : (
          <p>Country: {country}</p>
        )}
      </div>
    </div>
  );
}

const Dashboard = () => {
    const [nodeCount, setNodeCount] = useState(0);
    const [transactionCount, setTransactionCount] = useState(0);

    useEffect(() => {
        // Fetch node count from NodesDashboard endpoint
        axios.get('http://127.0.0.1:5000/get_nodes')
            .then(response => setNodeCount(response.data.nodes.length))
            .catch(error => console.error('Error fetching nodes:', error));

        // Fetch transaction count from lists endpoint
        axios.get("http://localhost:5000/get_chain")
            .then(response => {
                const transactions = response.data.chain.flatMap(block => block.transactions);
                setTransactionCount(transactions.length);
            })
            .catch(error => console.error('Error fetching transactions:', error));
    }, []);

    return (
        <div className="dashboard">
            <div className="dashboard-item animated">
                <h2>Registered Nodes</h2>
                <p className="count">{nodeCount}</p>
            </div>
            <div className="dashboard-item animated">
                <h2>Transactions Occurred</h2>
                <p className="count">{transactionCount}</p>
            </div>
            {/* Add more dashboard items as needed */}
        </div>
    );
};

export default Dashboard;
